$( ".unit-25" ).append("<div id='popupCntnr' class='btn' style='background-color : inherit;'></div>")
function giveAlert() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
}

$(function(){
setInterval(getLatestNetBuySellQty, 800);
console.log("WORKING...")
});

var BUY_MSG = '';
var SELL_MSG = '';
var ITER = 1;


function getLatestNetBuySellQty(){
	var ORDER_QTY = 15000;
	var NO_OF_ORDERS = 1;
	$('#instruments').each(function (){
		$(this).find('li').each(function(){
			var symbol = $(this).find('.symbol');
			var symNameSpan  = $(symbol).find('span');
			var symName = symNameSpan.text();
			//if(symName != '')
			//	alert(symName);
			var marketDepth = $(this).find('.market-depth');
			var marketDepthBuy = $(marketDepth).find('.market-depth-buy');
			var tbody = $(marketDepthBuy).find('tbody');
			$(tbody).find('tr').each(function(){
				var priceAt = $('td:nth-child(1)', this).text();
				var orderQnty = $('td:nth-child(2)', this).text();
				var totalOrders = $('td:nth-child(3)', this).text();
				if(orderQnty >= ORDER_QTY && totalOrders == NO_OF_ORDERS){
					var msg = symName+"\nBUY Price : "+priceAt+"\nQuantity : "+orderQnty+"\nNo. Orders : "+totalOrders;
					console.log(msg);
					if(BUY_MSG != msg){
						BUY_MSG = msg;
						$( "#popupCntnr" ).append( "<div id='ppDivBuy"+ITER+"' class='btn abcde' style='border-radius:10px; background: white; border: 1px solid #ccc; text-align:left; width:195px; background-color : #fff0e6' onclick='closeThisPP(id)'>"
													+   msg+"</div>" );
						giveAlert();
					}
				}
			});
			
			var marketDepthSell = $(marketDepth).find('.market-depth-sell');
			var SellTbody = $(marketDepthSell).find('tbody');
			$(SellTbody).find('tr').each(function(){
				var SellPriceAt = $('td:nth-child(1)', this).text();
				var SellOrderQnty = $('td:nth-child(2)', this).text();
				var SellTotalOrders = $('td:nth-child(3)', this).text();
				if(SellOrderQnty >= ORDER_QTY && SellTotalOrders == NO_OF_ORDERS){
					var msg2 = symName+"\nSELL Price : "+SellPriceAt+"\nQuantity : "+SellOrderQnty+" \nNo. Orders : "+SellTotalOrders;
					console.log(msg2);
					if(SELL_MSG != msg2){ 
						SELL_MSG = msg2;
						$( "#popupCntnr" ).append( "<div id='ppDivBuy"+ITER+"' class='btn abcde' style='border-radius:10px; background: white; border: 1px solid #ccc; text-align:left; width:195px; background-color : #fff0e6' onclick='closeThisPP(id)'>"
													+   msg2+"</div>" );
						giveAlert();
					}
				}
			});
			
		});
	});
	ITER++;
}
function closeThisPP(idVal){
	SELL_MSG = '';
	BUY_MSG = '';
	$('#'+idVal).remove();
	
}
function closeDiv(){
	console.log("Closing all popups");
	SELL_MSG = '';
	BUY_MSG = '';
	$('div.btn').hide();
}